bk_ports.body:
ports:
- name: ${name: Port Name}
  port: ${port: Port}
  protocol: TCP

bk_volumeMounts.body:
volumeMounts:
  - name: ${name: Name of the Volume}
  mountPath: ${path: Path to mount}

bk_volumes.body:
volumes:
  - name: ${name: name of the volume}
  mountPath: ${path: Path to mount}

bk_volumes_hostPath.body:
volumes:
  - name: ${name: Volume Name}
  hostPath:
    path: ${path: Path to mount}

bk_containers.body:
containers:
  - image: ${image: image}
  name: ${name: my-name}
  env:
    - name: ${ENVVARNAME: ENVVARNAME}
      value: ${ENVVARVALUE: ENVVARVALUE}

bk_env_from_secret.body:
env:
  - name: ${envvar: ENV_VAR}
    valueFrom:
      secretKeyRef:
        name: ${name: name}
        key: ${key: key}
